from employees
where salary between 5000 and 12000
and department_id in (20,50);
-- 7.	The HR department needs a report that displays the last name and hire date for all employees who were hired in 2005.
SELECT last_name, hire_date
from employees
where hire_date LIKE '%05';
-- 8.	Create a report to display the last name, salary, and commission of all employees who earn commissions. Sort data in descending order of salary and commissions. Use the columns numeric position in the order by clause.
select last_name, salary, commission_pct
from employees
where commission_pct is not null
order by 2 asc, 3 asc;
